public static boolean hasCycle(ListNode head) {
    ListNode slow = head;
    ListNode fast = head;

    while (fast != null && fast.next != null) {
        slow = slow.next;
        fast = fast.next.next; // Corrected line

        if (slow == fast)
            return true;
    }

    return false;
}
