#include <stdio.h>

int main() {
    int arr[] = {10, 20, 30, 40, 50};
    int n = 5;
    int target = 30;

    for (int i = 0; i < n; i++) { // The loop must run from i=0 to i < n (i.e., 0 to 4)
        if (arr[i] == target) {
            printf("Found at index %d", i);
            return 0;
        }
    }

    printf("Not found");
    return 0;
}
