#include <stdio.h>
#include <string.h>

int main(){
  char s[100];
  scanf("%s", s);
  int i, j;
  char temp;
  for(i=0, j=strlen(s)-1; i<j; i++, j--){
    temp = s[i];
    s[i] = s[j];
    s[j] = temp;
  }
  printf("%s", s);
}
